// Process delegation email send result
const emailResponse = $input.first().json;
const requestData = $('1. Format Delegation Email Content').first().json;

const success = !emailResponse.error && (emailResponse.messageId || emailResponse.id || emailResponse.conversationId);

const result = {
  success: success,
  status: success ? "sent" : "failed",
  partnerId: requestData.partnerId,
  delegateId: requestData.delegateId,
  email: requestData.email,
  delegateName: requestData.delegateName,
  partnerName: requestData.partnerName,
  contactId: requestData.contactId,
  emailType: requestData.emailType,
  subject: requestData.subject,
  preOnboardingLink: requestData.preOnboardingLink,
  messageId: emailResponse.messageId || emailResponse.id || emailResponse.conversationId || null,
  message: success ? "Partner delegation email sent successfully" : (emailResponse.error || emailResponse.message || "Failed to send delegation email"),
  timestamp: new Date().toISOString()
};

if (success) {
  console.log('‚úÖ Partner delegation email sent successfully!');
  console.log('Message ID:', result.messageId);
  console.log('Delegate Email:', result.email);
  console.log('Partner:', result.partnerName);
} else {
  console.error('‚ùå Failed to send partner delegation email:', emailResponse.error || emailResponse.message);
  result.error = emailResponse.error || emailResponse.message || 'Failed to send delegation email';
  result.ghlResponse = emailResponse;
}

return [{ json: result }];

