// Extract verification request from TPE
const data = $input.first().json.body;

console.log('üì± Sending verification code to:', data.phone);
console.log('üîë Code:', data.verificationCode);

if (!data.phone || !data.verificationCode) {
  throw new Error('Missing required fields: phone or verificationCode');
}

// Format phone for GHL (ensure +1 for US numbers)
let formattedPhone = data.phone.replace(/[^0-9+]/g, '');
if (!formattedPhone.startsWith('+')) {
  formattedPhone = '+1' + formattedPhone;
}

// Build SMS message
const message = data.message || `Your Power100 verification code is: ${data.verificationCode}\n\nThis code expires in 10 minutes.`;

return [{
  json: {
    contractorId: data.contractorId,
    name: data.name || 'Valued Contractor',
    phone: formattedPhone,
    originalPhone: data.phone,
    verificationCode: data.verificationCode,
    message: message,
    expiresAt: data.expiresAt,
    timestamp: new Date().toISOString()
  }
}];

// Process GHL response
const ghlResponse = $input.first().json;
const requestData = $node["Format Request"].json;

const success = !ghlResponse.error && (ghlResponse.messageId || ghlResponse.success);

const result = {
  success: success,
  contractorId: requestData.contractorId,
  phone: requestData.phone,
  verificationCode: requestData.verificationCode,
  messageId: ghlResponse.messageId || null,
  timestamp: new Date().toISOString()
};

if (success) {
  console.log('‚úÖ Verification SMS sent successfully');
  console.log('Message ID:', ghlResponse.messageId);
} else {
  console.error('‚ùå Failed to send SMS:', ghlResponse.error || 'Unknown error');
  result.error = ghlResponse.error || ghlResponse.message || 'Failed to send SMS';
}

return [{ json: result }];
