// Process email send result
const emailResponse = $input.first().json;
const requestData = $('1. Format Email Content').first().json;

const success = !emailResponse.error && (emailResponse.messageId || emailResponse.id || emailResponse.conversationId);

const result = {
  success: success,
  status: success ? "sent" : "failed",
  contractorId: requestData.contractorId,
  email: requestData.email,
  contactId: requestData.contactId,
  emailType: requestData.emailType,
  subject: requestData.subject,
  messageId: emailResponse.messageId || emailResponse.id || emailResponse.conversationId || null,
  message: success ? "Welcome email sent successfully" : (emailResponse.error || emailResponse.message || "Failed to send email"),
  timestamp: new Date().toISOString()
};

if (success) {
  console.log('‚úÖ Welcome email sent successfully!');
  console.log('Message ID:', result.messageId);
  console.log('Email:', result.email);
} else {
  console.error('‚ùå Failed to send welcome email:', emailResponse.error || emailResponse.message);
  result.error = emailResponse.error || emailResponse.message || 'Failed to send email';
  result.ghlResponse = emailResponse;
}

return [{ json: result }];

